ion registro_del(formId)			{				var form = document.getElementById(formId);				var input = form.getElementsByTagName('input');				var param = '';								for(i=0; i<input.length; i++)				{					if(input[i].type == 'checkbox' && input[i].checked == true )					{						param += input[i].name+';';					}									}				if( param == '')				{					return false;									}				else				{					request = createRequest();										if( request != null ){						var requestData = 'param='+param;						url='../server/registro_del.php';						url += (url.indexOf('?') === -1) ? '?' : '&amp;';						url += 'ridwes='+Math.random();						request.open('POST',url,true);						request.setRequestHeader('Content-type', 'application/x-www-form-urlencoded');						request.onreadystatechange = registro_Delresponse;						request.send(requestData);						}				}							}						function registro_Delresponse()			{				if(request.readyState == 4){					if(request.status == 200){						//alert(request.responseText);						window.location.reload();					}				}			}								</script>";				$pi .= "<div class='BtnButtomManage' onclick=\"location = 'index.php?actionType=gerenciar.eventos.add'\" title='".getLabel('LABEL_ADICIONAR', $_SESSION['LANGUAGE'])."'><span class='BtnProprio'>".getLabel('LABEL_ADICIONAR', $_SESSION['LANGUAGE'])."</span></div></li>";		$pi .= "<div class='BtnButtomManage' onclick=\"url('gerenciar.eventos.edit', '', '".getLabel('LABEL_JS_MAIS_DE_UM', $_SESSION['LANGUAGE'])."', '".getLabel('LABEL_JS_NENHUM', $_SESSION['LANGUAGE'])."', '".getLabel('LABEL_JS_UPDATE', $_SESSION['LANGUAGE'])."')\"  title='".getLabel('LABEL_EDITAR', $_SESSION['LANGUAGE'])."'><span class='BtnProprio'>".getLabel('LABEL_EDITAR', $_SESSION['LANGUAGE'])."</span></div></li>";		$pi .= "<div class='BtnButtomManage' onclick=\"url('gerenciar.eventos.descricao', '', '".getLabel('LABEL_JS_MAIS_DE_UM', $_SESSION['LANGUAGE'])."', '".getLabel('LABEL_JS_NENHUM', $_SESSION['LANGUAGE'])."', '".ge