tion>";										}					}				$pi .= "</select>";			}					$pi .= "</div>";				$pi .= "<div style='margin-bottom: 3px;'></div>";				$pi .= "<div class='EntradaTextForm'>";			if(!empty($_POST) && isset($_POST['ACTION']) )			{				$pi .= "<input class='LiloginText' style='width: 250px' type='text' id='nome' name='nome' value='".$_POST['nome']."' maxlength='70' placeholder='".getLabel('LABEL_NAME', $_SESSION['LANGUAGE'])."'/>";			}			else			{				$pi .= "<input class='LiloginText' style='width: 250px' type='text' id='nome' name='nome' value='' maxlength='70' placeholder='".getLabel('LABEL_NAME', $_SESSION['LANGUAGE'])."'/>";			}		$pi .= "</div>";        		$pi .= "<div style='margin-bottom: 3px;'></div>";		$pi .= "<div>";			if(!empty($_POST) && isset($_POST['ACTION']) )			{				( $_POST['status'] == 'on' ) ? $pi .= "<input type='checkbox' name='status' checked='true'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>" : $pi .= "<input type='checkbox' name='status'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>";			}			else			{				$pi .= "<input type='checkbox' name='status'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>";			}		$pi .= "</div>";				$pi .= "<div><br/></div>";		$pi .="<div><input type='hidden' name='ACTION' value='ACTION'/></div>";		$pi .="<div><input class='BtnProprio' type='submit' value='".getLabel('LABEL_ENVIAR', $_SESSION['LANGUAGE'])."'/></div>";		$pi .= "</form>";?>