<?php	acessoDireto( "ADMINISTRADOR;" );		$row = mysql_query("SELECT 	quartos.CODQUARTO,	quartos.NOME,	DATE_FORMAT( quartos.DTA, '%d/%m/%Y - %Hh%i' ) as DTA,	quartos.STATUS,	quartos.OWNER,	quartos_tipo.NOME as TIPO,	quartos_tipo.CODQUARTOTIPO	FROM quartos	INNER JOIN quartos_rel_quartos_tipo ON quartos_rel_quartos_tipo.CODQUARTO=quartos.CODQUARTO	INNER JOIN quartos_tipo ON quartos_rel_quartos_tipo.CODQUARTOTIPO=quartos_tipo.CODQUARTOTIPO	WHERE quartos.CODQUARTO='".$_GET['codquarto']."'	GROUP BY quartos.CODQUARTO	ORDER BY quartos.NOME ASC	");		if(mysql_num_rows($row)!=0)	{		$row = mysql_fetch_object($row);	}		$erro = "";		if ( !empty($_POST) && isset($_POST['ACTION']) ) 	{		$codquarto = tratapost($_GET['codquarto']);		$codquartotipo = tratapost($_POST['codquartotipo']);				$nome = trim( htmlentities( tratapost($_POST['nome']) ) );		$status = trim( tratapost($_POST['status']) );				( $status == 'on' ) ? $status = 1 : $status = 0;				mysql_query("UPDATE `quartos`SET `NOME`='$nome', `STATUS`='$status' WHERE `CODQUARTO`='$codquarto'");			mysql_query("UPDATE quartos_rel_quartos_tipo SET CODQUARTOTIPO='".$_POST['codquartotipo']."' WHERE `CODQUARTO`='$codquarto'");					echo "<script>alert('* ".getLabel('LABEL_EDIT_SUCESSO_LABEL', $_SESSION['LANGUAGE'])."!')</script>";		echo "<script>window.location = 'index.php?actionType=gerenciar.quartos';</script>";		exit();	} 	else 	{		$erro = getLabel('ERRO_PREENCHER_TODOS_CAMPOS', $_SESSION['LANGUAGE']);	}		$pi = "<form name='formCpf' method='post' action='index.php?actionType=gerenciar.quartos.edit&codquarto=".$_GET['codquarto']."'>";		$pi .= "<div class='ErroMessage'>* $erro!</div>";		$pi .= "<br/>";		$pi .= "<div style='margin-bottom: 3px;'></div>";				$pi .= "<div class='EntradaTextForm'>";				$quartos = mysql_query("SELECT * FROM quartos_tipo WHERE STATUS=1 ORDER BY NOME ASC");						if(mysql_num_rows($quartos) !=0)			{				$pi .= "<select class='LiloginText' style='width: 250px' name='codquartotipo' id='codquartotipo' >";					while( $quarto = mysql_fetch_object($quartos) )					{						if(!empty($_POST) && isset($_POST['ACTION']) )						{							( $_POST['codquartotipo'] == $quarto->CODQUARTOTIPO ) 							? $pi .= "<option value='$quarto->CODQUARTOTIPO' selected>$quarto->NOME</option>"											: $pi .= "<option value='$quarto->CODQUARTOTIPO'>$quarto->NOME</option>";										}						else						{							( $row->CODQUARTOTIPO == $quarto->CODQUARTOTIPO ) 							? $pi .= "<option value='$quarto->CODQUARTOTIPO' selected>$quarto->NOME</option>"											: $pi .= "<option value='$quarto->CODQUARTOTIPO'>$quarto->NOME</option>";									}					}				$pi .= "</select>";			}					$pi .= "</div>";				$pi .= "<div class='EntradaTextForm'>";			if(!empty($_POST))			{				$pi .= "<input class='LiloginText' style='width: 250px' type='text' id='nome' name='nome' value='".$_POST['nome']."' maxlength='70' placeholder='".getLabel('LABEL_NAME', $_SESSION['LANGUAGE'])."'/>";			}			else			{				$pi .= "<input class='LiloginText' style='width: 250px' type='text' id='nome' name='nome' value='$row->NOME' maxlength='70' placeholder='".getLabel('LABEL_NAME', $_SESSION['LANGUAGE'])."'/>";			}		$pi .= "</div>";        		$pi .= "<div style='margin-bottom: 3px;'></div>";		$pi .= "<div>";			if(!empty($_POST))			{				( $_POST['status'] == 'on' && $row->STATUS == '1') ? $pi .= "<input type='checkbox' name='status' checked='true'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>" : $pi .= "<input type='checkbox' name='status'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>";			}			else			{				( $row->STATUS == '1') ? $pi .= "<input type='checkbox' name='status' checked='true'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>" : $pi .= "<input type='checkbox' name='status'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>";			}		$pi .= "</div>";				$pi .= "<div><br/></div>";		$pi .="<div><input type='hidden' name='ACTION' value='ACTION'/></div>";		$pi .="<div><input class='BtnProprio' type='submit' value='".getLabel('LABEL_ENVIAR', $_SESSION['LANGUAGE'])."'/></div>";		$pi .= "</form>";	?>