<?php	acessoDireto( "ADMINISTRADOR;" );		$row = mysql_query("SELECT quartos_tipo.*, DATE_FORMAT( quartos_tipo.DTA, '%d/%m/%Y - %H:%i:%s' ) as DTA FROM quartos_tipo WHERE quartos_tipo.CODQUARTOTIPO='".$_GET['codquartotipo']."' ORDER BY NOME ASC");	if(mysql_num_rows($row)!=0)	{		$row = mysql_fetch_object($row);	}		$erro = "";		if ( !empty($_POST) && isset($_POST['ACTION']) ) 	{		$codquartotipo = tratapost($_GET['codquartotipo']);				$nome = trim( htmlentities( tratapost($_POST['nome']) ) );		$sigla = trim( strtoupper( htmlentities( tratapost($_POST['sigla']) ) ) );		$status = trim( tratapost($_POST['status']) );				( $status == 'on' ) ? $status = 1 : $status = 0;		mysql_query("UPDATE `quartos_tipo`SET `NOME`='$nome', `STATUS`='$status', `SIGLA`='$sigla' WHERE `CODQUARTOTIPO`='$codquartotipo'");					echo "<script>alert('* ".getLabel('LABEL_EDIT_SUCESSO_LABEL', $_SESSION['LANGUAGE'])."!')</script>";		echo "<script>window.location = 'index.php?actionType=gerenciar.quartos.tipos';</script>";		exit();	} 	else 	{		$erro = getLabel('ERRO_PREENCHER_TODOS_CAMPOS', $_SESSION['LANGUAGE']);	}		$pi = "<form name='formCpf' method='post' action='index.php?actionType=gerenciar.quartos.tipos.edit&codquartotipo=".$_GET['codquartotipo']."'>";		$pi .= "<div class='ErroMessage'>* $erro!</div>";		$pi .= "<br/>";		$pi .= "<div style='margin-bottom: 3px;'></div>";				$pi .= "<div class='EntradaTextForm'>";			if(!empty($_POST))			{				$pi .= "<input class='LiloginText' style='width: 250px' type='text' id='sigla' name='sigla' value='".$_POST['sigla']."' maxlength='70' placeholder='".getLabel('LABEL_SIGLA', $_SESSION['LANGUAGE'])."'/>";			}			else			{				$pi .= "<input class='LiloginText' style='width: 250px' type='text' id='sigla' name='sigla' value='$row->SIGLA' maxlength='70' placeholder='".getLabel('LABEL_SIGLA', $_SESSION['LANGUAGE'])."'/>";			}		$pi .= "</div>";				$pi .= "<div class='EntradaTextForm'>";			if(!empty($_POST))			{				$pi .= "<input class='LiloginText' style='width: 250px' type='text' id='nome' name='nome' value='".$_POST['nome']."' maxlength='70' placeholder='".getLabel('LABEL_NAME', $_SESSION['LANGUAGE'])."'/>";			}			else			{				$pi .= "<input class='LiloginText' style='width: 250px' type='text' id='nome' name='nome' value='$row->NOME' maxlength='70' placeholder='".getLabel('LABEL_NAME', $_SESSION['LANGUAGE'])."'/>";			}		$pi .= "</div>";        		$pi .= "<div style='margin-bottom: 3px;'></div>";		$pi .= "<div>";			if(!empty($_POST))			{				( $_POST['status'] == 'on' && $row->STATUS == '1') ? $pi .= "<input type='checkbox' name='status' checked='true'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>" : $pi .= "<input type='checkbox' name='status'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>";			}			else			{				( $row->STATUS == '1') ? $pi .= "<input type='checkbox' name='status' checked='true'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>" : $pi .= "<input type='checkbox' name='status'/> <span class='Userlabel'>".getLabel('LABEL_ATIVO_TEXT', $_SESSION['LANGUAGE'])."</span>";							}		$pi .= "</div>";				$pi .= "<div><br/></div>";		$pi .="<div><input type='hidden' name='ACTION' value='ACTION'/></div>";		$pi .="<div><input class='BtnProprio' type='submit' value='".getLabel('LABEL_ENVIAR', $_SESSION['LANGUAGE'])."'/></div>";		$pi .= "</form>";?>