<?php	acessoDireto( "ADMINISTRADOR;" );		$pi = "<div style='margin-bottom: 10px;'>";		$pi .= "<form name='formsearch1' method='post' action='index.php?actionType=".$_GET['actionType']."'>";			$pi .= "<table>";					$pi .= "<tr>";					$pi .= "<td>";											$pi .= "<div class='EntradaTextForm'>";							if(!empty($_POST) && isset($_POST['ACTION']))							{								$pi .= "<div class='Floatbusca'><input class='LiloginText' type='text' name='buscar' value='$_POST[buscar]' placeholder='".getLabel('LABEL_BUSCAR', $_SESSION['LANGUAGE'])."'/></div>";								$pi .= "<select name='status' id='status'>";																		switch($_POST["status"])									{										case "":											$stt = "selected";										break;										case "3":											$stt0 = "selected";										break;										case "4":											$stt1 = "selected";										break;									}																	$pi .= "<option value='' $stt>--</option>";									$pi .= "<option value='3' $stt0>booking.com</option>";									$pi .= "<option value='4' $stt1>decolar.com</option>";								$pi .= "</select>";																$pi .= "<div class='Floatbusca'><input class='BtnProprio' type='submit' value='".getLabel('LABEL_ENVIAR', $_SESSION['LANGUAGE'])."'/></div>";							}							else							{								$pi .= "<div class='Floatbusca'><input class='LiloginText' type='text' name='buscar' value='' placeholder='".getLabel('LABEL_BUSCAR', $_SESSION['LANGUAGE'])."'/></div>";																$pi .= "<select name='status' id='status'>";									$pi .= "<option value=''>--</option>";									$pi .= "<option value='3'>booking.com</option>";									$pi .= "<option value='4'>decolar.com</option>";								$pi .= "</select>";																$pi .= "<div class='Floatbusca'><input class='BtnProprio' type='submit' value='".getLabel('LABEL_ENVIAR', $_SESSION['LANGUAGE'])."'/></div>";							}							$html .= "<div class='ResetFloat'></div>";																				$pi .= "</div>";											$pi .= "</td>";				$pi .= "</tr>";			$pi .= "</table>";				$pi .="<div><input type='hidden' name='ACTION' value='BUSCAR'/></div>";		$pi .= "</form>";	$pi .= "</div>";		$pi .= "<form name='List' method='post'>";													$pi .= "<div style='float: left; cursor: pointer; margin-right: 5px;' onclick=\"location = 'index.php?actionType=gerenciar.reservas.externas.add'\" title='".getLabel('LABEL_ADICIONAR', $_SESSION['LANGUAGE'])."'><span class='BtnProprio'>".getLabel('LABEL_ADICIONAR', $_SESSION['LANGUAGE'])."</span></div>";				//$pi .= "<div class='BtnButtomManage' onclick=\"url('', '', '".getLabel('LABEL_JS_MAIS_DE_UM', $_SESSION['LANGUAGE'])."', '".getLabel('LABEL_JS_NENHUM', $_SESSION['LANGUAGE'])."', '".getLabel('LABEL_JS_UPDATE', $_SESSION['LANGUAGE'])."')\"  title='".getLabel('LABEL_EDITAR', $_SESSION['LANGUAGE'])."'><span class='BtnProprio'>".getLabel('LABEL_GER_RESERVAS', $_SESSION['LANGUAGE'])."</span></div></li>";		//$pi .= "<div class='BtnButtomManage' onclick=\"url('gerenciar.reservas.descricao', '', '".getLabel('LABEL_JS_MAIS_DE_UM', $_SESSION['LANGUAGE'])."', '".getLabel('LABEL_JS_NENHUM', $_SESSION['LANGUAGE'])."', '".getLabel('LABEL_JS_UPDATE', $_SESSION['LANGUAGE'])."')\"  title='".getLabel('LABEL_DESCRICAO', $_SESSION['LANGUAGE'])."'><span class='BtnProprio'>".getLabel('LABEL_VISUALIZAR', $_SESSION['LANGUAGE'])."</span></div></li>";		$pi .= "<div class='ResetFloat'></div>";		$pi .= "<div class='MasterList'>";					$pi .= "<table class='Tableinter'>";				$pi .= "<tr>";										$pi .= "<td valign='top'>";												$criterio = "";																		if( $_POST['status'] != "" || $_POST['buscar'] != "" )						{														$xnome = trim( htmlentities( $_POST['buscar'] ) );														$dtainicio = formataDataForUSA($xnome);							$dtafim = formataDataForUSA($xnome);							$real = limpaValorReal($xnome);														$criterio .= "AND ";														if( $_POST['status'] != "" && $_POST['buscar'] != "" )							{								$criterio .= " valor.VALOR_TOTAL LIKE '%$real%' 								OR valor.VALOR_TOTAL LIKE '%$xnome%' 								OR valor.N_CONHECIMENTO LIKE '%$xnome%' 								OR reservas.DTAINICIO LIKE '%$dtainicio%' 								OR reservas.DTAFIM LIKE '%$dtafim%'								AND reservas.STATUS='".$_POST['status']."'";								}							elseif( $_POST['status'] != "" )							{								$criterio .= " reservas.STATUS='".$_POST['status']."'";							}							elseif( $_POST['buscar'] != "" )							{								$criterio .= " valor.VALOR_TOTAL LIKE '%$real%' 								OR valor.VALOR_TOTAL LIKE '%$xnome%' 								OR valor.N_CONHECIMENTO LIKE '%$xnome%' 								OR reservas.DTAINICIO LIKE '%$dtafim%'								OR reservas.DTAFIM LIKE '%$dtainicio%'								";								}														 						}												$result = mysql_query("SELECT 						reservas.*,												valor.VALOR_TOTAL,												valor.N_CONHECIMENTO,						DATE_FORMAT( reservas.DTA, '%d/%m/%Y<br/>%Hh%i' ) as DTA,												DATE_FORMAT( reservas.DTA, '%Y-%m-%d' ) as DTARESERVA						FROM reservas						INNER JOIN reservas_rel_valor ON reservas_rel_valor.CODRESERVA=reservas.CODRESERVA												INNER JOIN valor ON reservas_rel_valor.CODVALOR=valor.CODVALOR												WHERE reservas.RESERVAS_EXTERNAS=1												$criterio						ORDER BY (reservas.DTA) DESC						");								$pi .=  "<div class='separador'></div>";						$pi .=  "<div>";						$pi .= "<table class='Tableinter'>";							$pi .=  "<tr id='adminTopTable'>";								$pi .=  "<td style='width: 10px;'></td>								<td>".getLabel('LABEL_DTA_CADASTRO', $_SESSION['LANGUAGE']).":</td>								<td>Cod.:</td>																<td>".getLabel('LABEL_DTA_INICIO', $_SESSION['LANGUAGE']).":</td>																<td>".getLabel('LABEL_DTA_FIM', $_SESSION['LANGUAGE']).":</td>																<td>".getLabel('LABEL_SITUACAO', $_SESSION['LANGUAGE']).":</td>								<td>".getLabel('LABEL_CRIADO_POR', $_SESSION['LANGUAGE']).":</td>";							$pi .=  "</tr>";													$i = 0;						while( $qtos_tipo = mysql_fetch_object( $result ) ) {							if ( $i % 2 == 0 ) 							{								$pi.=  "<tr class='LinhaPar'>";							} 							else 							{								$pi.=  "<tr class='LinhaImpar'>";							}																$pi .= "<td align='right'><input type='checkbox' name='$qtos_tipo->CODRESERVA'/></td>";									$pi .= "<td>$qtos_tipo->DTA</td>";									$pi .= "<td>".$qtos_tipo->N_CONHECIMENTO."</td>";																		$pi .= "<td>".formataDataForBrazil($qtos_tipo->DTAINICIO)."</td>";																		$pi .= "<td>".formataDataForBrazil($qtos_tipo->DTAFIM)."</td>";																											if((int)$qtos_tipo->STATUS == 0)									{										$adianta_2_dias = mysql_query("SELECT DATE_ADD('$qtos_tipo->DTARESERVA', INTERVAL 2 DAY) as DTA");										$adianta_2_dias = mysql_fetch_object($adianta_2_dias);																				$checkdatas = mysql_query("SELECT TIMESTAMPDIFF(DAY, CURRENT_DATE, '$adianta_2_dias->DTA') as CKECKDATAS");										if(mysql_num_rows($checkdatas) != 0)										{											$checkdatas = mysql_fetch_object($checkdatas);											if( (int)$checkdatas->CKECKDATAS < 0 )											{												//cancelamento automático da reserva												mysql_query("UPDATE reservas SET STATUS=2 WHERE CODRESERVA='$qtos_tipo->CODRESERVA'");												$qtos_tipo->STATUS = 2;											}										}									}																		switch((int)$qtos_tipo->STATUS)									{										case 0:											$status = "<td style='background: green;'><span style='color: #fff;'>".getLabel('LABEL_RESERVAS_PRE', $_SESSION['LANGUAGE'])."</span></td>";										break;										case 1:											$status = "<td style='background: yellow;'><span style='color: #003580;'>".getLabel('LABEL_RESERVA_CONFIRMADA', $_SESSION['LANGUAGE'])."</span></td>";										break;										case 2:											$status = "<td style='background: red;'><span style='color: #fff;'>".getLabel('LABEL_RESERVA_CANCELADA', $_SESSION['LANGUAGE'])."</span></td>";										break;										case 3:											$status = "<td style='background: #DAA300;'><span style='color: #003580;'>booking.com</span></td>";										break;										case 4:											$status = "<td style='background: #84a8fb;'><span style='color: #ef1b24;'>decolar.com</span></td>";										break;									}																		$pi .= "$status";																		$owner = mysql_query("SELECT login.LOGIN FROM login									INNER JOIN login_rel_pessoa ON login_rel_pessoa.CODLOGIN=login.CODLOGIN									WHERE login_rel_pessoa.CODPESSOA='$qtos_tipo->OWNER'");																		$owner = mysql_fetch_object($owner);									$pi .=  "<td>$owner->LOGIN</td>";								$pi .=  "</tr>";														$i++;												}													$pi .=  "</table>";						$pi .=  "</div>";										$pi .= "</td>";									$pi .= "</tr>";			$pi .= "</table>";		$pi .= "</div>";	$pi .= "</form>";		$html .= getNavegacao($pi, getLabel('LABEL_GERENCIAR_RESERVAS_EXTERNAS', $_SESSION['LANGUAGE']));?>