<?php        	$html .= "<div id='corpo'>";		    $html .="<div class='container'>";    	$html .="<div class='fix_form col-xs-4' style='top: 0px;'></div>";                $html .="<div class='col-xs-4'>";                    require("side.topicos.php");                    $html .= "</div>";                $html .="<div class='eventos-view pag_interna col-xs-8 right'>";  		$listRespostas = mysql_query("SELECT               eventos.CODEVENTO,               eventos.".$_SESSION['LANGUAGE']." as TDICA,               eventos.ORDEM,              eventos.CODEVENTO,              descricao.".$_SESSION['LANGUAGE']." as TDESC,               fotos.URL               FROM eventos              INNER JOIN eventos_rel_descricao ON eventos_rel_descricao.CODEVENTO=eventos.CODEVENTO              INNER JOIN descricao ON eventos_rel_descricao.CODDESCRICAO=descricao.CODDESCRICAO              INNER JOIN eventos_rel_fotos ON eventos_rel_fotos.CODEVENTO=eventos.CODEVENTO              INNER JOIN fotos ON eventos_rel_fotos.CODFOTO=fotos.CODFOTO              WHERE eventos.STATUS=1              AND fotos.DESTAQUE=1              AND eventos.CODEVENTO='".$_GET["codevento"]."'              ORDER BY (eventos.ORDEM+0) ASC");              if(mysql_num_rows($listRespostas) != 0)               {                				while($resposta = mysql_fetch_object($listRespostas))                {                  (string) $nome = getlinguagemdasessao();                  $html .="<div style='margin-top: 35px;'>";                  // ================== THUMB ==================                  $fotos = mysql_query("SELECT                    fotos.*,                        DATE_FORMAT( fotos.DTA, '%d/%m/%Y - %Hh%i' ) as DTA                  FROM fotos                   INNER JOIN eventos_rel_fotos ON eventos_rel_fotos.CODFOTO=fotos.CODFOTO                  WHERE eventos_rel_fotos.CODEVENTO='$resposta->CODEVENTO'                  ORDER BY (fotos.DESTAQUE) DESC            