sql_fetch_object($tipo);									}																		/*									 * checa por meio das datas se existe o quarto da suite em questao ocupado									 */									$arrs = mysql_query("SELECT qts.CODQUARTO FROM quartos AS qts 										INNER JOIN reservas_rel_quartos AS rrq ON rrq.CODQUARTO=qts.CODQUARTO 										INNER JOIN reservas AS rsv ON rrq.CODRESERVA=rsv.CODRESERVA 										WHERE 										rsv.DTAINICIO BETWEEN '{$_POST['dtainicio']}' AND '{$_POST['dtainicio']}' 										AND 										rsv.DTAFIM BETWEEN '{$_POST['dtafim']}' AND '{$_POST['dtafim']}' 										AND 										(rsv.STATUS>=1 OR rsv.STATUS=3 OR rsv.STATUS=4 OR rsv.STATUS=5 OR rsv.STATUS=6)");																			/*									 * prepara uma string com chaveamento dos quartos da categoria em questao que nao pode ser									 * ocupado pois ja possui visitantes									 */									$emenda = "";											if( mysql_num_rows($arrs) != 0)									{										while( $arr = mysql_fetch_object($arrs))										{											$emenda .= "'{$arr->CODQUARTO}'";										}									}																		/*									 * monta string com as chaves e um not in mysql pra separar por criterios									 * foi feito desta maneira pois nem o BETWEEN juntamente com SELECT e NOT IN estao funcionando corretamente 									 */									if( $emenda != "" )									{										$emenda = str_replace("''","','", $emenda);										$emenda = "AND quartos.CODQUARTO NOT IN({$emenda})";									}																			/*									 * renderiza a selecao de quartos disponiveis para os ocupantes									 */									$quarto = mysql_query("SELECT quartos.CODQUARTO, quartos.NOME, quartos.PESSOAS FROM quartos 									INNER JOIN quartos_rel_quartos_tipo ON quartos.CODQUARTO=quartos_rel_quartos_tipo.CODQUARTO 									WHERE quartos_rel_quartos_tipo.CODQUARTOTIPO='{$_POST['codquartotipo']}' 									{$emenda}									ORDER BY (quartos.PESSOAS+0) ASC");																		$qntd = ((int)$_POST["adulto"]+(int)$_POST["crianca0a5"]+(int)$_POST["crianca6a12"]+(int)$_POST["crianca12"]);																		if( mysql_num_rows($quarto) != 0)									{										while( $q = mysql_fetch_object($quarto) )										{											if((int)$qntd <= (int)$q->PESSOAS )											{												$codquarto = $q->CODQUARTO;												break;											}										}									}																		/*									 * aqui pega o codigo da reserva (chave)									 */									$codreserva = mysql_query("										SELECT reservas.CODRESERVA FROM 										reservas										INNER JOIN reservas_rel_valor ON reservas_rel_valor.CODRESERVA=reservas.CODRESERVA										INNER JOIN valor ON reservas_rel_valor.CODVALOR=valor.CODVALOR										WHERE valor.N_CONHECIMENTO='{$_POST['N_CONHECIMENTO']}'									");																		if(mysql_num_rows($codreserva) != 0)									{										$codreserva = mysql_fetch_object($codreserva);									}																		/*									 * colocar a reserva em um dos quartos									 * alterar o status da reserva dinamicamente									 */																		if($codquarto != "")									{										mysql_query("UPDATE reservas SET STATUS=1 WHERE CODRESERVA='{$codreserva->CODRESERVA}'");										mysql_query("INSERT INTO `reservas_rel_quartos` (`CODRESERVA`,`CODQUARTO`,`REFERENCIA`)VALUES('{$codreserva->CODRESERVA}','{$codquarto}','1')");									}																		/*									 * dispara o e-mail confirmando a reserva									 * 									 */									require("email_conf.php");									//var_dump($_POST);																		/*									array(122) { 									["codigoBandeira"]=> string(10) "mastercard" 									["cartaoNumero"]=> string(19) "5453 0100 0006 6167" 									["cartaoValidade"]=> string(9) "05 / 2018" 									["cartaoCodigoSeguranca"]=> string(3) "123" 									["nomeTitulo"]=> string(13) "Fulano de Tal" 									["formaPagamento"]=> string(1) "1" 									["capturarAutomaticamente"]=> string(4) "true" 									["tentarAutenticar"]=> string(3) "sim